const setLoading = () => dispatch({ type: SET_LOADING});

return (
  <GithubContext.Provider
  value={{
    users: state.users,
    user: state.user,
    repos: state.repos,
    loading: state.loading,
    searchUsers,
    clearUsers
  }}
  >
  {props.children}
  </GithubContext.Provider>
);
}
