import React, { Component } from 'react';

class UserItem extends Component{
      state = {
      id: '1',
      login: 'Bojurie',
      avatar_url: '',
      html_url: 'https://github.com/Bojurie'
    }
  render(){
    const {login, html_url, avatar_url  } = this.state;

    return <div className="card text-center">
        <img src = {avatar_url} alt = '' className = 'round-img' style = {{
        width: '60px'}}/>
        <h3>{login}</h3>
        <a href={html_url} className='btn btn-dark btn-sm my-1' target='blank'>More</a>
      </div>;

  }
}
export default UserItem;
